package repaso_tema1;

public class Ej2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 144;
		int y = 999;
		
		System.out.println("x = " + x);
		System.out.println("y = " + y);
		System.out.println(x + " + " + y + " = " + (x+y));
		System.out.println(x + " - " + y + " = " + (x-y));
		System.out.println(x + " x " + y + " = " + (x*y));
		System.out.println(x + " / " + y + " = " + (x/y));
	}

}
