package ejercicios_iniciales;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	double x = 144;
	double y = 999;
	
	
	System.out.println("El valor de x es de: " + x + ", El valor de y es de: " + y);
	System.out.println("La suma de ambas es: " + (x + y));
	System.out.println("La resta entre ellas es de: " + (x-y));
	System.out.println("La división entre ellas da: " + (x/y) );
	System.out.println("La multipicación entre ellas da: " + (x*y) );
	}

}